CREATE TABLE "Treinos" (
	"ID" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"Data_Treino" DATE NOT NULL,
	"Exercicios" TEXT,
	"Observacao" TEXT,
	"ID_Paciente" INTEGER NOT NULL UNIQUE,
	"ID_Personal" INTEGER NOT NULL UNIQUE,
	PRIMARY KEY("ID", "ID_Paciente", "ID_Personal")
);

CREATE TABLE "Nutricionistas" (
	"ID" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"Nome" VARCHAR(255) NOT NULL,
	"Idade" DATE NOT NULL,
	"Endereço" VARCHAR(255) NOT NULL,
	"Telefone" VARCHAR(255) NOT NULL,
	"Email" VARCHAR(255) NOT NULL,
	"CRN" VARCHAR(255) NOT NULL,
	"Senha_hash" TEXT NOT NULL,
	"Foto" BYTEA NOT NULL,
	"Data_cadastro" DATE NOT NULL,
	PRIMARY KEY("ID")
);

CREATE TABLE "Personal" (
	"ID" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"Nome" VARCHAR(255) NOT NULL,
	"Idade" DATE NOT NULL,
	"Endereço" VARCHAR(255) NOT NULL,
	"Telefone" VARCHAR(255) NOT NULL,
	"Email" VARCHAR(255) NOT NULL,
	"CREF" VARCHAR(255) NOT NULL,
	"Senha_hash" TEXT NOT NULL,
	"Foto" BYTEA NOT NULL,
	"Data_cadastro" DATE NOT NULL,
	PRIMARY KEY("ID")
);

CREATE TABLE "Clientes" (
	"ID" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"Nome" VARCHAR(255) NOT NULL,
	"Sexo" VARCHAR(255) NOT NULL,
	"Idade" DATE NOT NULL,
	"Endereço" VARCHAR(255) NOT NULL,
	"Telefone" VARCHAR(255) NOT NULL,
	"Email" VARCHAR(255) NOT NULL,
	"Turno" VARCHAR(255) NOT NULL,
	"Senha_hash" TEXT NOT NULL,
	"Foto" BYTEA,
	"Data_cadastro" DATE NOT NULL,
	"Observacao" TEXT,
	PRIMARY KEY("ID")
);

CREATE TABLE "Consultas" (
	"ID" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"Data_consulta" DATE NOT NULL,
	"Peso" DECIMAL,
	"Altura" DECIMAL,
	"IMC" DECIMAL,
	"Cintura" DECIMAL,
	"Quadril" DECIMAL,
	"Observacao" TEXT,
	"Fotos" BYTEA,
	"Prontuario" BYTEA,
	"Consulta_Agendada" DATE,
	"ID_Paciente" INTEGER NOT NULL UNIQUE,
	"ID_Nutricionista" INTEGER NOT NULL UNIQUE,
	"ID_Personal" INTEGER NOT NULL UNIQUE,
	"ID_Treinos" INTEGER NOT NULL UNIQUE,
	PRIMARY KEY("ID", "ID_Paciente", "ID_Nutricionista", "ID_Personal", "ID_Treinos")
);

ALTER TABLE "Nutricionistas"
ADD FOREIGN KEY("ID") REFERENCES "Consultas"("ID_Nutricionista")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Personal"
ADD FOREIGN KEY("ID") REFERENCES "Consultas"("ID_Personal")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Pacientes ou Clientes"
ADD FOREIGN KEY("ID") REFERENCES "Consultas"("ID_Paciente")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Treinos"
ADD FOREIGN KEY("ID") REFERENCES "Consultas"("ID_Treinos")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Personal"
ADD FOREIGN KEY("ID") REFERENCES "Treinos"("ID_Personal")
ON UPDATE NO ACTION ON DELETE NO ACTION;

ALTER TABLE "Pacientes ou Clientes"
ADD FOREIGN KEY("ID") REFERENCES "Treinos"("ID_Paciente")
ON UPDATE NO ACTION ON DELETE NO ACTION;
